        public bool IsHappy(int n)
        {
            HashSet<int> hash = new HashSet<int>();
            int sum = 0;
            int tmp, mod;
            while (n != 1)
            {
                while (n != 0)
                {
                    tmp = n / 10;
                    mod = n - tmp * 10;
                    sum += mod * mod;
                    n = tmp;
                }

                if (hash.Contains(sum))
                {
                    return false;
                }
                else
                {
                    hash.Add(sum);
                }

                n = sum;
                sum = 0;
            }

            return true;
        }


        public int MajorityElement(int[] nums)
        {

            return findMaj(nums, 0, nums.Length - 1);
        }

        private int findMaj(int[] nums, int start, int end)
        {

            

            //base case
            if (end - start < 8)
            {
                Dictionary<int, int> dict = new Dictionary<int, int>();
                for (int i = start; i <= end; i++)
                {
                    if (dict.ContainsKey(nums[i]))
                    {
                        dict[nums[i]]++;
                    }
                    else
                    {
                        dict.Add(nums[i], 1);
                    }
                }

                int maxKey = 0, maxVal = 0;
                foreach (int key in dict.Keys)
                {
                    if (dict[key] > maxVal)
                    {
                        maxKey = key;
                        maxVal = dict[key];
                    }
                }
                return maxKey;

            }
            else
            {
                // first 1/3
                int idx1 = start + (end - start) / 3;
                int val1 = findMaj(nums, start, idx1);

                // mid 1/3
                int idx2 = start + (end - start) / 3 * 2;
                int val2 = findMaj(nums, idx1 + 1, idx2);

                // last 1/3
                int val3 = findMaj(nums, idx2 + 1, end);

                if (val1 == val2 || val1 == val3) return val1;
                else return val2;

            }

        }



        IList<IList<int>> result;
        public IList<IList<int>> PathSum(TreeNode root, int sum)
        {
            result = new List<IList<int>>();
            if (root == null) return result;
            IList<int> path = new List<int>();
            Traverse(root, sum, path);
            return result;
        }

        private void Traverse(TreeNode node, int sum, IList<int> path)
        {

            //base case
            if (node.left == null && node.right == null && sum == node.val)
            {
                IList<int> onePath = new List<int>(path);
                onePath.Add(node.val);
                result.Add(onePath);
            }

            path.Add(node.val);

            if (node.left != null)
            {
                Traverse(node.left, sum - node.val, path);
            }

            if (node.right != null)
            {
                Traverse(node.right, sum - node.val, path);
            }

            path.Remove(path.Count - 1);

        }

        

